// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

// imports
import "@openzeppelin/contracts/access/Ownable.sol";
import "hardhat/console.sol";

/**
 * @notice Functions helper
 */
library WheelHelper {
  /**
   * @notice Pick a prize based on weights
   * @dev This func uses a math formula
   */
  function pickPositionByWeight(
    uint256 numChoices,
    uint256[] memory choiceWeight,
    uint256 randomNumber
  ) internal pure returns (uint256) {
    uint256 sum_of_weight = 0;
    for (uint256 i = 0; i < numChoices; i++) {
      sum_of_weight += choiceWeight[i];
    }
    uint256 rnd = randomNumber % sum_of_weight;
    for (uint256 i = 0; i < numChoices; i++) {
      if (rnd < choiceWeight[i]) return i;
      rnd -= choiceWeight[i];
    }
    return rnd;
  }

  /**
   * @notice Send Transaction
   * @dev Transfer money from contract to player
   */
  function sendPrize(uint256 prizeAmount, address payable playerAddress) internal {
    uint256 amount = prizeAmount + tx.gasprice;
    (bool success, ) = playerAddress.call{value: amount}("");
    if (!success) {
      revert Wheel__TransferFailed();
    }
  }
}

// errors
error Wheel__NotEnoughToEnter();
error Wheel__PlayerNoBalance();
error Wheel__NoBalance();
error Wheel__NotOpen();
error Wheel__NotEntered();
error Wheel__TransferFailed();
error Wheel__Blacklisted();

/** @title Wheel of fortune contract
 * @author 0xL
 * @notice This manages a decentralized wheel of fortune game
 * @dev This contract implements Chainlink VRF to pick the wheel prize position
 */

contract Wheel is Ownable {
  // types
  enum WheelState {
    IDDLE,
    OPEN,
    CLOSED
  }

  enum PlayerState {
    READY,
    SPINING,
    UNBALANCED
  }

  enum WheelPrize {
    X2,
    WIN5,
    LOSE2,
    WIN10,
    RUGGED,
    WIN50,
    FREESPIN,
    WIN20,
    BLOCKED,
    WIN10ALL,
    LUCK,
    LOSE3
  }

  // state variables
  uint256 private immutable i_minBalance;
  uint256 private immutable i_enterFee;
  uint256 private immutable i_spinFee;
  WheelState private s_wheelState;
  uint256 private s_playersCount = 0;
  uint256[] private s_prizesWeight = [1, 2, 10, 2, 50, 1, 15, 1, 5, 0, 20, 10];
  mapping(address => Player) private s_addressToPlayer;
  mapping(uint256 => uint256) private s_requestIdToRandomWord;
  mapping(uint256 => address) private s_requestIdToRequester;
  mapping(address => bool) private s_addressToBlocked;
  string[] internal s_prizes = [
    "X2",
    "WIN5",
    "LOSE2",
    "WIN10",
    "RUGGED",
    "WIN50",
    "FREESPIN",
    "WIN20",
    "BLOCKED",
    "WIN10ALL",
    "LUCK",
    "LOSE3"
  ];

  // structs
  struct Player {
    uint256 balance;
    PlayerState state;
    WheelPrize lastPrize;
  }

  // events
  event WheelIsOpen();
  event WheelEnter(address indexed player);
  event WheelRequestedNumber(uint256 indexed requestId);
  event WheelPrizeSent(address indexed player, WheelPrize indexed prize);
  event WheelClosed();
  event WheelZeroBalance();
  event WheelFilled(address indexed owner, uint256 amount);

  constructor(
    address vrfCoordinatorV2,
    uint256 enterFee,
    uint256 spinFee
  ) payable {
    i_enterFee = enterFee;
    i_spinFee = spinFee;
    i_minBalance = enterFee * 10;
    s_wheelState = WheelState.IDDLE;
  }

  // Payable functions

  /**
   * @notice Player enters the wheel
   * @dev Set player in mapping and updates players counter
   */
  function enter() public payable {
    if (s_wheelState != WheelState.OPEN) {
      revert Wheel__NotOpen();
    }

    if (msg.value < i_enterFee) {
      revert Wheel__NotEnoughToEnter();
    }

    if (s_addressToBlocked[msg.sender]) {
      revert Wheel__Blacklisted();
    }

    s_addressToPlayer[msg.sender] = Player(i_enterFee, PlayerState.READY, WheelPrize.RUGGED);
    s_playersCount++;
    emit WheelEnter(msg.sender);
  }

  /**
   * @notice Player spins the wheel
   * @dev Reduce players balance/state, request Chainlink VRF
   */
  function spin() public payable {
    if (s_wheelState != WheelState.OPEN) {
      revert Wheel__NotOpen();
    }

    if (s_addressToPlayer[msg.sender].balance < i_spinFee) {
      revert Wheel__PlayerNoBalance();
    }

    if (s_addressToBlocked[msg.sender]) {
      revert Wheel__Blacklisted();
    }

    s_addressToPlayer[msg.sender].state = PlayerState.SPINING;
    s_addressToPlayer[msg.sender].balance -= i_spinFee;

    if (s_addressToPlayer[msg.sender].balance == 0) {
      s_addressToPlayer[msg.sender].state = PlayerState.UNBALANCED;
    }

    // uint256 requestId = i_vrfCoordinator.requestRandomWords(
    //   i_keyHash,
    //   i_subscriptionId,
    //   REQUEST_CONFIRMATIONS,
    //   i_callbackGasLimit,
    //   NUM_WORDS
    // );

    // s_requestIdToRequester[requestId] = msg.sender;
    // emit WheelRequestedNumber(requestId);
  }

  // close wheel and withdraw remaning funds
  /**
   * @notice Owner closes the wheel
   * @dev Updates wheel state to CLOSED and withdraw all funds to owner
   */
  function close() public onlyOwner {
    s_wheelState = WheelState.CLOSED;
    uint256 balance = address(this).balance;
    if (balance == 0) {
      emit WheelZeroBalance();
    }
    if (balance > 0) {
      (bool success, ) = owner().call{value: address(this).balance}("");
      if (!success) {
        revert Wheel__TransferFailed();
      }
    }
    emit WheelClosed();
  }

  // internal functions

  /**
   * @notice Filter Chainlink VRF random number
   * @dev Reduce players balance/state, request Chainlink VRF
   */
  // function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords) internal override {
  //   // console.log("RANDOM WORDS", randomWords[0]);
  //   address playerAddress = s_requestIdToRequester[requestId];
  //   uint256 pickedPrize = WheelHelper.pickPositionByWeight(
  //     s_prizes.length,
  //     s_prizesWeight,
  //     randomWords[0]
  //   );
  //   // console.log("PRIZE", pickedPrize);
  //   deliveryPrize(payable(playerAddress), WheelPrize(pickedPrize));
  // }

  /**
   * @notice Delivers the player's prize
   * @dev Check picked random prize and deliver prize to player
   */
  function deliveryPrize(address payable playerAddress, WheelPrize prize) internal {
    Player memory player = s_addressToPlayer[playerAddress];

    if (prize == WheelPrize.X2) {
      WheelHelper.sendPrize(player.balance * 2, playerAddress);
    } else if (prize == WheelPrize.WIN5) {
      WheelHelper.sendPrize(i_spinFee * 5, playerAddress);
    } else if (prize == WheelPrize.LOSE2) {
      s_addressToPlayer[playerAddress].balance -= (i_spinFee * 2);
    } else if (prize == WheelPrize.WIN10) {
      WheelHelper.sendPrize(i_spinFee * 10, playerAddress);
    }
    if (prize == WheelPrize.RUGGED) {
      //
    } else if (prize == WheelPrize.WIN50) {
      WheelHelper.sendPrize(i_spinFee * 50, playerAddress);
    } else if (prize == WheelPrize.FREESPIN) {
      player.balance += i_spinFee;
    } else if (prize == WheelPrize.BLOCKED) {
      s_addressToBlocked[playerAddress] = true;
    } else if (prize == WheelPrize.WIN10ALL) {
      //
    } else if (prize == WheelPrize.LUCK) {
      // player is really luck, lose everything LOL
    } else if (prize == WheelPrize.LOSE3) {
      player.balance -= i_spinFee * 3;
    }

    player.lastPrize = prize;

    if (player.state == PlayerState.SPINING) {
      player.state = PlayerState.READY;
    }

    s_addressToPlayer[playerAddress] = player;
    emit WheelPrizeSent(playerAddress, prize);
  }

  // view functions

  function getEnterFee() public view returns (uint256) {
    return i_enterFee;
  }

  function getSpinFee() public view returns (uint256) {
    return i_spinFee;
  }

  function getWheelState() public view returns (WheelState) {
    return s_wheelState;
  }

  function getPlayer(address player) public view returns (Player memory) {
    return s_addressToPlayer[player];
  }

  function getNumberOfPlayers() public view returns (uint256) {
    return s_playersCount;
  }

  function isBlocked(address player) public view returns (bool) {
    return s_addressToBlocked[player];
  }

  function getPrizes() public view returns (string[] memory) {
    return s_prizes;
  }

  function getBalance() public view returns (uint256) {
    return address(this).balance;
  }

  // fallback
  receive() external payable onlyOwner {
    if (msg.value >= i_minBalance) {
      s_wheelState = WheelState.OPEN;
      emit WheelIsOpen();
    }

    emit WheelFilled(owner(), msg.value);
  }
}

